import E from 'wangeditor';
export default {
  data() {
    return {
      editor: null,
      user: JSON.parse(localStorage.getItem('xm-user') || '{}'),
      tableData: [],
      pageNum: 1,
      pageSize: 5,
      total: 0,
      fromVisible: false,
      viewVisible: false,
      form: {},
      viewData: null
    };
  },
  mounted() {
    this.load(1);
  },
  // methods：本页面所有的点击事件或者其他函数定义区
  methods: {
    initWangEditor(content) {
      this.$nextTick(() => {
        this.editor = new E('#editor');
        this.editor.config.placeholder = '请输入内容';
        this.editor.config.uploadFileName = 'file';
        this.editor.config.uploadImgServer = 'http://localhost:9090/files/wang/upload';
        this.editor.create();
        setTimeout(() => {
          this.editor.txt.html(content);
        });
      });
    },
    viewEditor(content) {
      this.viewData = content;
      this.viewVisible = true;
    },
    load(pageNum) {
      if (pageNum) this.pageNum = pageNum;
      this.$request.get('/lost/selectPage', {
        params: {
          pageNum: this.pageNum,
          pageSize: this.pageSize,
          userId: this.user.id
        }
      }).then(res => {
        this.tableData = res.data?.list;
        this.total = res.data?.total;
      });
    },
    handleAdd() {
      this.form = {};
      this.initWangEditor('');
      this.fromVisible = true;
    },
    handleEdit(row) {
      this.form = JSON.parse(JSON.stringify(row));
      this.initWangEditor(this.form.content || '');
      this.fromVisible = true;
    },
    del(id) {
      this.$confirm('您确定删除吗？', '确认删除', {
        type: "warning"
      }).then(response => {
        this.$request.delete('/lost/delete/' + id).then(res => {
          if (res.code === '200') {
            // 表示操作成功
            this.$message.success('操作成功');
            this.load(1);
          } else {
            this.$message.error(res.msg); // 弹出错误的信息
          }
        });
      }).catch(() => {});
    },
    save() {
      this.form.content = this.editor.txt.html();
      this.$request({
        url: this.form.id ? '/lost/update' : '/lost/add',
        method: this.form.id ? 'PUT' : 'POST',
        data: this.form
      }).then(res => {
        if (res.code === '200') {
          // 表示成功保存
          this.$message.success('保存成功');
          this.fromVisible = false;
        } else {
          this.$message.error(res.msg); // 弹出错误的信息
        }
      });
    },

    handleCurrentChange(pageNum) {
      this.pageNum = pageNum;
      this.load(this.pageNum);
    },
    handleImgSuccess(res) {
      this.form.img = res.data;
    }
  }
};
